import styled from "styled-components";
import StoreItem from "./StoreItem";
import StoresByType from "./StoresByType";
import React, { useState } from 'react';

const StoresOverview = (props) => {
    const infoType = ["명소", "카페", "음식점", "놀거리"];
    const [selectedInfoType, setSelcetedInfoType] = useState(null);

    const container = null;
    return (
        <Container>
            {
                (props.selectedCityOrDistrict !== null && 
                <CityOrDistrictTxt>{props.selectedCityOrDistrict}</CityOrDistrictTxt>)
            }       
            {    
                infoType.map((type) => {
                if (selectedInfoType === null || selectedInfoType === type) {
                    return (
                        <div key={type}>
                            <StoresByType type={type} onMoreClick={() => setSelcetedInfoType(type)} selectedInfoType={selectedInfoType} selectedCityOrDistrict={props.selectedCityOrDistrict}/>
                            {
                                infoType.indexOf(type) !== infoType.length - 1 && selectedInfoType === null &&
                                <Divider />
                            }
                        </div>
                    );
                } else {
                    return null;
                }
            })}

        </Container>
    );
}

const Container = styled.div`
    width: 100%;
    margin-left: 20px;
    margin-top: 10px;
    margin-right: 40px;
`;

const CityOrDistrictTxt = styled.div`
    font-size: 25px;
    font-weight: 700;
    margin-bottom: 8px;
    margin-top: 13px;
    margin-left: 20px;
`;

const Divider = styled.div`
    width: 100%;
    height: 2px;
    background-color: #61677A; // 원하는 색상으로 변경
`;

export default StoresOverview;
