import styled from "styled-components";
import StoreItem from "./StoreItem";
import dummydata from "../../assets/data/dummydata.json";

const StoresByType = (props) => {
    return (
        <StoresByTypeContainer>
            <TypeTxt>{props.type}</TypeTxt>
            <StoreItemContainer>
                {dummydata.map((store, index) => {
                    return store.info_type === props.type &&
                    <StoreItem key={store.reg_id} img="" name={store.nickname} rating={store.rating}/>
                })}
            </StoreItemContainer>
            <Divider />
        </StoresByTypeContainer>
    );
}

const StoresByTypeContainer = styled.div`
    display: flex;
    flex-direction: column;
    margin-bottom: 13px;
`;

const StoreItemContainer = styled.div`
    display: flex;
    flex-direction: row;
`;

const TypeTxt = styled.div`
    font-size: 25px;
    font-weight: 700;
    margin-bottom: 8px;
`;

const Divider = styled.div`
  height: 1px;
  background-color: #ccc; // 원하는 색상으로 변경
  margin: 16px 0; // 원하는 마진으로 변경
`;

export default StoresByType;
